INSERT INTO `CHNQuestion` VALUES (1,'请说出“铁骑突出刀枪鸣”的上一句','银瓶乍破水浆迸',11),(2,'请说出“轻舟已过万重山”的上一句','两岸猿声啼不住',6),(3,'请说出“铜雀春深锁二乔”的上一句','东风不与周郎便',9),(4,'请说出“秦时明月汉时关”的下一句','万里长征人未还',9);
