INSERT INTO `Game` VALUES (3,'{\"title\": \"蛋炒饭\",\"endings\": [1, 3, 4, 5, 6],\"0\": {\"content\": \"蛋炒饭是我国传统美食，它的做法很简单，相信你一定可以选出正确的流程。首先是食材的选择，你会用哪种米饭呢，新鲜米饭，还是隔夜米饭？\", \"choices\": {\"新鲜米饭\": 1, \"隔夜米饭\": 2}},\"1\": {\"content\":\"你的蛋炒饭失去了灵魂。\"},\"2\": {\"content\": \"现在你开始炒饭，将米粒均匀沾上鸡蛋液，进行翻炒。现在你可以选择加一些其他东西，你会放什么，酱油，火腿，青椒，或者奶油？\", \"choices\": {\"酱油\": 3, \"火腿\": 4, \"青椒\": 5, \"奶油\": 6}},\"3\": {\"content\": \"你得到了美味的酱油炒饭！\"},\"4\": {\"content\": \"你得到了香喷喷的扬州炒饭！\"},\"5\": {\"content\": \"你得到了神奇的青椒炒饭！\"},\"6\": {\"content\": \"你是魔鬼吗，总算知道意大利人对菠萝披萨的看法了。\"}}');
/*!40000 ALTER TABLE `Game` ENABLE KEYS */;
UNLOCK TABLES;

